import { BUS } from '@/events'

export default {
  data () {
    return {
      serverData: null,
      fields: [
        {
          key: 'name',
          label: 'name',
          sortable: true,
          sortDirection: 'asc'
        },
        'actions'
      ]
    }
  },

  computed: {
    /**
     * Table items, computed from the server data datasets.
     *
     * @returns {*[]|*}
     */
    items () {
      if (!this.serverData) {
        return []
      }
      return this
        .serverData.datasets
        .map(dataset => {
          return {
            name: dataset['ds.name']
          }
        })
        .sort((a, b) => {
          return a.name.localeCompare(b.name)
        })
    },
    isBusy () {
      return this.serverData === null
    }
  },

  beforeRouteEnter (from, to, next) {
    next(async vm => {
      await vm.initializeData()
      BUS.$on('connection:reset', vm.initializeData)
    })
  },

  async beforeRouteUpdate (from, to, next) {
    this.initializeData()
    next()
  },

  beforeRouteLeave (from, to, next) {
    this.serverData = null
    BUS.$off('connection:reset')
    next()
  },

  methods: {
    initializeData () {
      const vm = this
      vm.$fusekiService
        .getServerData()
        .then(serverData => {
          vm.serverData = serverData
        })
    }
  }
}
